<template>
  <div
    v-if="!editMode"
    :class="['palette-item__color', {'palette-item__color--big': isBig}]"
    :style="{backgroundColor: '#' + color}"
    :data-clipboard-text="selectedColor"
  ></div>
  <div v-else
    :class="['color-picker', {'color-picker--big': isBig}]"
    :style="{backgroundColor: currentColor}">
    <label class="color-picker__label">
      <input class="color-picker__input" type="color" :value="currentColor" @input="setCurrentColor($event.target.value)">
    </label>
  </div>
</template>

<script>
export default {
  props: {
    editMode: {
      type: Boolean,
      required: true,
    },
    color: {
      type: String,
      required: true,
    },
    isBig: {
      type: Boolean,
      default: false,
    },
  },
  data() {
    return {
      currentColor: this.color,
    };
  },
  computed: {
    selectedColor() {
      return this.$store.state.selectedColor;
    },
  },
  methods: {
    setCurrentColor(color) {
      this.currentColor = color;
      this.$emit('colorWasChanged', color.slice(1));
    },
  },
};
</script>

<style scoped>
.palette-item__color, .color-picker {
  height: 100px;
  width: 100px;
  border-radius: 10%;
  margin: 10px;
  cursor: url('data:;base64,'), pointer;
}

.palette-item__color--big, .color-picker--big {
  width: 160px;
}

.palette-item__color:hover {
  border: 2px solid #d55;
}

.color-picker {
  cursor: pointer;
  display: flex;
}

.color-picker__input {
  visibility: hidden;
  width: 0;
  height: 0;
  position: absolute;
  top: 0;
  left: 0;
}

.color-picker__label {
  cursor: pointer;
  position: relative;
  flex: 1 1 0px;
}
</style>
